sys_variable_value.value:
(function execute(inputs, outputs) {
    /*
     * Needed to get access to deployment request sys_id. Attempts to access sys_id in previous script would return undefined.
     * Assuming this is a timing issue when creating the deployment request.

       NOTE: The url from inputs is no longer being used in this script. See: DEF0187977
       We will use the PipelineService to retrieve the prod url instead
     */
  if (!inputs.has_error) {
    var prodUrl = '';

    try {
      var pipelineService = new PipelineService();
      prodUrl = pipelineService.getEnvironmentUrl(PipelineConstants.PROD_ENV_COLUMN);
    } catch(error) {
      outputs['has_error'] = true;
      outputs['error_message'] = error.name;
      outputs['status_code'] = error.code;
      return;
    }

  	outputs['deployment_request_url'] = prodUrl + 'nav_to.do?uri=/sn_app_eng_studio_deployment_request.do?sys_id=' + JSON.parse(inputs.body)['result'].sys_id;
  	outputs['number'] = JSON.parse(inputs.body)['result'].number;
  } else {
  	outputs['has_error'] = inputs.has_error;
    outputs['error_message'] = inputs.error_message;
    outputs['status_code'] = inputs.status_code;
  }

})(inputs, outputs);

