sys_variable_value.value:
(function execute(inputs, outputs) {

  // NOTE: The url from inputs is no longer being used in this script. See: DEF0187977
  // We will use the PipelineService to retrieve the prod url instead

  var sysApp = new GlideRecord("sys_app");
  if (!sysApp.get(inputs.application_sys_id)) {
  	outputs['has_error'] = true;
  	outputs['error_message'] = gs.getMessage("Application with sys_id {0} does not exist", inputs.application_sys_id);
  	outputs['status_code'] = 404;
    return;
  }

  var prodUrl = '';

  try {
  	var pipelineService = new PipelineService();
  	prodUrl = pipelineService.getEnvironmentUrl(PipelineConstants.PROD_ENV_COLUMN);
  } catch(error) {
    outputs['has_error'] = true;
    outputs['error_message'] = error.name;
    outputs['status_code'] = error.code;
    return;
  }

  var provider = new sn_cc.StandardCredentialsProvider();
  var credential = provider.getCredentialByID(inputs.prod_cred_sys_id);

  var deploymentRequest = new sn_ws.RESTMessageV2();
  deploymentRequest.setEndpoint(prodUrl + 'api/now/table/sn_app_eng_studio_deployment_request');
  deploymentRequest.setHttpMethod('POST');
  deploymentRequest.setBasicAuth(credential.getAttribute("user_name"), credential.getAttribute("password"));

  deploymentRequest.setRequestHeader("Accept","application/json");
  deploymentRequest.setRequestHeader('Content-Type','application/json');

  var deploymentRequestBody = inputs.request_data;
  deploymentRequestBody.app_sys_id = inputs.application_sys_id;

  deploymentRequest.setRequestBody(JSON.stringify(deploymentRequestBody));

  var deploymentRequestResponse = deploymentRequest.execute();

  var parsedBody;
  var errorMessage;

  try {
    parsedBody = JSON.parse(deploymentRequestResponse.getBody());
    errorMessage = deploymentRequestResponse.haveError() && parsedBody.error && parsedBody.error.detail ? 'Error Detail: ' + parsedBody.error.detail  + ' ; Error Message: ' + deploymentRequestResponse.getErrorMessage() : deploymentRequestResponse.getErrorMessage();
  } catch (err) {
    errorMessage = deploymentRequestResponse.getErrorMessage();
  }

  outputs['body'] = deploymentRequestResponse.getBody();
  outputs['has_error'] = deploymentRequestResponse.haveError();
  outputs['error_message'] =  errorMessage;
  outputs['status_code'] = deploymentRequestResponse.getStatusCode();
  outputs['requestor_name'] = deploymentRequestBody.requestor_first_name + ' ' + deploymentRequestBody.requestor_last_name;
  outputs['app_name'] = deploymentRequestBody.app_name;

})(inputs, outputs);

