sys_variable_value.value:
(function execute(inputs, outputs) {
	var userSysId = inputs['user_sys_id'];
	var tokenPayload = inputs['token_payload'];
  	var userSyncService = new UserSyncService();
  	var wasSuccessfullyCreated = true;

	var guuid = gs.generateGUID();
	var tempPass = guuid.substring(0, 6);

	var responseStatusCode = userSyncService.setSyncUserTempPassword({
      	sysId: userSysId,
    	tempPass: tempPass,
		tokenPayload: tokenPayload,
    });

    if (responseStatusCode !== 200) {
		tempPass = null;
      	wasSuccessfullyCreated = false;
    }

	outputs['temp_pass'] = tempPass;
  	outputs['was_successfully_created'] = wasSuccessfullyCreated;
})(inputs, outputs);

