sys_variable_value.value:
(function execute(inputs, outputs) {
	try {
      	var to = inputs['to'];
      	var subject = inputs['subject'];
      	var emailTemplate = inputs['email_template'];
        var deploymentRequestTaskNumber = inputs['deployment_request_task_number'];
      	var body = inputs['body'];

        var pipelineService = new PipelineService();
        var aesUtils = new AesUtils();
		var sendEmailAPIRequest = pipelineService.buildRequest(
            'POST',
            PipelineConstants.PROD_ENV_COLUMN,
            '/api/sn_app_eng_studio/generic/send_email',
			{ requestBody:
             	{
                    to: to,
                    subject: subject,
                    emailTemplate: emailTemplate,
                  	body: body
	            }
            }
        );
		var response = sendEmailAPIRequest.execute();
    }
	catch (e) {
		gs.warn(e.toString());

		return;
    }

  	var status = response.getStatusCode();

  	if (status !== 200) {
		gs.warn('E-mail to alert admin of deployment request not sent for deployment request ' + deploymentRequestTaskNumber);

		return;
    }
})(inputs, outputs);

