sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// remove trailing slashes
	current.instance_url = current.instance_url.replace(/\/+$/, '');

	var instance_url_str = current.instance_url.toString();
	if(instance_url_str !== previous.instance_url.toString()) {

        var protocols = ['http://', 'https://'];
        var startsWithProtocol = false;

        // Validate one of the protocols is on the instance_url
        for( var i = 0; i < protocols.length; i++){
          if(startsWithProtocol)
            break;

          startsWithProtocol = instance_url_str.toLowerCase().startsWith( protocols[i].toLowerCase());
        }

        if(!startsWithProtocol){
            var protocolsStr = protocols.map(function(protocol){return '"' + protocol +  '"';}).join(', ');
            gs.addErrorMessage(gs.getMessage("The 'instance_url' update failed because it does not start with a valid protocol: {0}", protocolsStr));
            current.setAbortAction(true);
            return;
        }

		var gr = new GlideRecord('sn_app_eng_studio_environment');
		gr.query();

		while(gr.next()) {
			if(instance_url_str === gr.instance_url.toString()){
				gs.addErrorMessage(gs.getMessage("The 'instance_url' update failed because the value is required to be unique. Found duplicate url: '{0}' on '{1}'",
                  [gr.instance_url.toString(), gr.getDisplayValue('sys_name')]));
				current.setAbortAction(true);
			}
		}
	}

})(current, previous);
