sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var nowTimeStamp = new GlideDateTime();
	var payload;
	var queueItem;
	var userSyncService = new UserSyncService();
	var userQueueService = new UserQueueService();

	var pipelineService;
	try {
		pipelineService = new PipelineService();
	}
	catch(ex) {
		gs.warn(ex.toString() + "\n\n" + gs.getProperty("glide.servlet.uri") + current.getLink());
	}

	// If there isn't an active pipeline at the time it is assumed
	// this is production and the item is added to the queue. This is to avoid losing changes before
	// the pipeline is setup.
	if (pipelineService && !pipelineService.isProd()) { 
		gs.info('Skipping AES queue sys_user_grmember Business Rule. Not production environment. ');
		return; 
	}

	if (current.operation() == 'insert') {

		// An insert for sys_user always added to the queue is to ensure 
		// the user always exists when adding to a group. If the user already 
		// exists then the insertion isn't executed. There isn't an business rule on sys_user 
		// for insert because it isn't known if the user will be in AES group or not at that point in time.
		var user = userSyncService.getUser('sys_id', current.user);

		// Creating a new instance here to modify the seconds. References are bad in this case.
		// this ensures the user is always inserted before the group.
		var userInsertTimeStamp = new GlideDateTime(current.sys_updated_on.toString());
		userInsertTimeStamp.addSeconds(-1);

		var userQueueItem =  new SyncQueueItem({
			operation: 'insert',
			table: 'sys_user',
			payload: userSyncService.buildUserPayload(user),
			sequence: userInsertTimeStamp,
		});

		queueItem = new SyncQueueItem({
			operation: 'insert',
			table: current.getTableName(),
			payload: userSyncService.buildUserGroupPayload(current),
			sequence: current.sys_updated_on,
		});


		userQueueService.insertQueue(userQueueItem);
		userQueueService.insertQueue(queueItem);

	}
	else if (current.operation() == 'delete') {
		payload = userSyncService.buildUserGroupPayload(current);

		queueItem = new SyncQueueItem({
			operation: 'delete',
			table: current.getTableName(),
			payload: payload,
			sequence: nowTimeStamp,
		});

		userQueueService.insertQueue(queueItem);
	}

})(current, previous);
