sys_script_include.script:
var ApplicationService = (function() {
    return {

        getAccessibleApps: function() {
            return gs.getSession().getApplicationPickerList();
        },

        getAccessibleAppSysIds: function() {
            return this.getAccessibleApps().map(function(app) {
                return app.sysId;
            });
        },

        /** 
         * @return {string} array of sys_ids of sys_scope records for applications user can read 
         */

        getUserApplications: function() {
            var apps = new GlideRecord('sys_app');
            var userApps = [];
            apps.query();

            while (apps.next()) {
                if (apps.canRead())
                    userApps.push(apps.getValue("sys_id"));
            }

            return userApps;
        }
    };
})();
