sys_script_include.script:
var AppPromotionAISearchPublishing = Class.create();
AppPromotionAISearchPublishing.prototype = {
    initialize: function() {
    },

	// Take a sys_app sys_id and publish the dictionaries, profiles, and indexes required for searching
	publish: function (appSysId) {
		try {
			sn_fd.FlowAPI.startAction('global.publish_app_template_ai_search_configuration', {'app_sys_id': appSysId});
		} catch (err) {
			gs.error("The search publishing flow encountered an error: " + err.toString());
		}
	},

	publishToTestInstance: function (appSysId) {
		try {
			var pipelineService = new PipelineService();

			var publishSearchRequest = pipelineService.buildRequest(
				"POST", 
				PipelineConstants.TEST_ENV_COLUMN,
				'/api/sn_app_eng_studio/applications/' + appSysId + '/publish_search_config'
			);

			var publishResponse = publishSearchRequest.execute();

			if (publishResponse.haveError()) {
				var error = new Error(publishResponse.getErrorMessage());
				error.code = publishResponse.getStatusCode();

				throw error;
			}

		} catch (err) {
			gs.error(err.toString());
		}
	},

    type: 'AppPromotionAISearchPublishing'
};
