sys_script_include.script:
var AesAppToTestEmail = Class.create();
AesAppToTestEmail.prototype = {
    initialize: function(deploymentRequest) {
		this.deploymentRequest = deploymentRequest;
    },
	getBody: function() {
        var aesEmailScript = new AesEmailScript();
		var style = aesEmailScript.getStyles();
        var deploymentRequest = this.deploymentRequest;
        var userName = deploymentRequest.requestor_user_name;
		var adminEmailPropertyValue = gs.getProperty('sn_app_eng_studio.aes_admin_contact');
		var htmlLinkStart = "";
		var htmlLinkEnd = "";

		if (adminEmailPropertyValue) {
			htmlLinkStart = '<a style="' + style.link + '" href="mailto:' + adminEmailPropertyValue + '">';
			htmlLinkEnd = '</a>';
		}

		var adminContactMessage = gs.getMessage('If you have questions on the status of your app, {0}contact your administrator{1}.', [htmlLinkStart, htmlLinkEnd]);
		var footerMessage = gs.getMessage('You have received this email to update you about your recent App Engine Studio creation.');
		var headerMessage = gs.getMessage('{0} is being reviewed by your admin', deploymentRequest.app_name);
		var innerBody = gs.getMessage("{0}Your administrator is currently reviewing and testing your application. Once it's approved, your admin will publish your app and you will be notified of its approval. You do not need to do anything at this time. {2}{3}", ['<div>', deploymentRequest.app_name, adminContactMessage, '</div><br />']);

		return aesEmailScript.getBody(userName, headerMessage, innerBody, footerMessage);
	},

    type: 'AesAppToTestEmail'
};
