sys_script_include.script:
var AesNotifyUserOfAESAccessEmail = Class.create();
AesNotifyUserOfAESAccessEmail.prototype = {
    initialize: function(emailProps) {
		this.userFirstName = emailProps.userFirstName || 'AES User';
		this.devUrl = emailProps.devUrl;
		this.tempPass = emailProps.tempPass;
    },

	getBody: function() {
		var divStartTag = '<div>';
		var divEndTag = '</div>';
		var brTag = '<br/>';

		var aesEmailScript = new AesEmailScript();
        var style = aesEmailScript.getStyles();
		var headerMessage = gs.getMessage('You now have access to App Engine Studio');
		var innerBody = '';

		innerBody += gs.getMessage('{0}App Engine Studio is a fast, intuitive, low-code visual development environment that empowers you to build applications that can transform your world of work.{1}', [divStartTag, divEndTag]);

		if (this.tempPass) {
			var passwordHtml = '<span style="font-size: 18px">' +  this.tempPass + '</span>';

			innerBody += brTag;
			innerBody += gs.getMessage('{0}Your temporary password is: {1}{2}', [divStartTag, passwordHtml, divEndTag]);
		}

		var linkToAppEngineStudio = '<a style="' + style.link + '" href="' + this.devUrl + 'now/appenginestudio/home" target="_blank">App Engine Studio</a>';
		innerBody += gs.getMessage('{0}{1}To start building apps, go to {2}{3}', [brTag, divStartTag, linkToAppEngineStudio, divEndTag]);


		return aesEmailScript.getBody(this.userFirstName, headerMessage, innerBody, '');
	},
    type: 'AesNotifyUserOfAESAccessEmail'
};
