sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	// Check if user has the "Submit for Deployment" delegated dev permission
	var access = gs.hasRightsTo("api/sn_app_eng_studio.applications.deployment_request/POST", {scopeId: request.pathParams.application_sys_id});
	if (!access) {
		throw new sn_ws_err.ServiceError()
			.setStatus(403)
			.setMessage(gs.getMessageLang("Access denied. Submit for Deployment delegated development permission required.", "en"))
			.setDetail(gs.getMessage("Access denied. Submit for Deployment delegated development permission required."));
	}

	var aesUtils = new AesUtils();

	// Much of this is available in the DeploymentHelpers script include sys_script_include_b853de3b53741010b846ddeeff7b1207
	// Consider refactoring in Rome
	var prodBaseInstanceUrl = null;
	var prodCredRef = null;
	var pipelineRecord = new GlideRecord('sn_app_eng_studio_pipeline');
	var applicationStatusHelper = new ApplicationStatusHelper;

	pipelineRecord.addQuery('active', true);
	pipelineRecord.query();
	if (pipelineRecord.getRowCount() === 0) {
		throw new sn_ws_err.ServiceError()
			.setStatus(500)
			.setMessage(gs.getMessageLang("No active pipeline found in sn_app_eng_studio_pipeline.", "en"))
			.setDetail(gs.getMessage("No active pipeline found in sn_app_eng_studio_pipeline."));
	}
	while (pipelineRecord.next()) {
		prodBaseInstanceUrl = pipelineRecord.prod_environment.getRefRecord().instance_url;
		prodCredRef = pipelineRecord.prod_environment.getRefRecord().instance_credential;
	}

	if (!prodBaseInstanceUrl || !prodCredRef) {
		// Usually happens if the pipeline has no prod environment.
		throw new sn_ws_err.ServiceError()
			.setStatus(500)
			.setMessage(gs.getMessageLang("Prod environment has not been setup correctly in sn_app_eng_studio_pipeline.", "en"))
			.setDetail(gs.getMessage("Prod environment has not been setup correctly in sn_app_eng_studio_pipeline."));
	}

	var prodCredSysID = null;
	var discoveryRecord = new GlideRecord('discovery_credentials');
	discoveryRecord.addQuery('tag', prodCredRef);
	discoveryRecord.query();
	if (discoveryRecord.getRowCount() === 0) {
		throw new sn_ws_err.ServiceError()
			.setStatus(500)
			.setMessage(gs.getMessageLang("No credentials found for prod environment in active pipeline.", "en"))
			.setDetail(gs.getMessage("No credentials found for prod environment in active pipeline."));
	}

	while (discoveryRecord.next()) {
		prodCredSysID = discoveryRecord.getValue('sys_id');
	}

	/**
	 * Get admin e-mail address from prod
	 */
	var path = '/api/sn_app_eng_studio/generic/configuration';
	var adminEmail = '';
	var remoteInstanceResponse = aesUtils.getResponseFromRemoteInstanceFlow(prodBaseInstanceUrl, path, prodCredSysID);

	if (remoteInstanceResponse && remoteInstanceResponse.has_error) {
		var error = new sn_ws_err.ServiceError();
		error.setStatus(remoteInstanceResponse.status_code);
		error.setMessage(remoteInstanceResponse.error_message);

		return response.setError(error);

	} else if (remoteInstanceResponse && remoteInstanceResponse.body) {
		var configuration = JSON.parse(remoteInstanceResponse.body).result;
		for(var i = 0; i < configuration.length; i++) {
			if (configuration[i].name === 'sn_app_eng_studio.aes_admin_contact') {
				adminEmail = configuration[i].value;
				break;
			}
		}
	}

	var inputs = {
		prod_cred_sys_id: prodCredSysID,
		url: prodBaseInstanceUrl,
		request_data: request.body.data,
		application_sys_id: request.pathParams.application_sys_id,
		admin_email: adminEmail
	};

	// Add user information to request
	var users = new GlideRecord("sys_user");
	var userAttributes;
	var attributeValue;

	if (users.get(gs.getUserID())) {
		userAttributes = ["email", "user_name", "first_name", "last_name"];
		userAttributes.forEach(function (attribute) {
			if (!users[attribute].nil()) {
				inputs.request_data["requestor_" + attribute] = users.getValue(attribute);
			}
		});
	}

	// Get application files
	var files = FileDataBuilder.build([request.pathParams.application_sys_id]);

	// Create array of objects with object name and launch url
	// [{ name : "Workspace", url: "/api/now/workspace_administration/workspace_url/8735d7f2b7215010835d6f54ce11a934" }]
	var launchUrls = files.reduce(function(prev, current) {
		if (current.name && current.launch_url) {
			prev.push({ name: current.name, url: current.launch_url });
		}
		return prev;
	}, []);

	if (launchUrls.length) {
		inputs.request_data.launch_urls = JSON.stringify(launchUrls);
	}

	// Run an action from a server-side script synchronously.
	var deploymentRequestResponse = sn_fd.FlowAPI.executeSubflow('sn_app_eng_studio.deployment_request_subflow', inputs);

	if (deploymentRequestResponse.has_error) {
		var error = new sn_ws_err.ServiceError();
		error.setStatus(deploymentRequestResponse.status_code);
		error.setMessage(deploymentRequestResponse.error_message);

		return response.setError(error);
	}

	var responseBody = JSON.parse(deploymentRequestResponse.body).result;
	var key = responseBody.state;

	responseBody.status = applicationStatusHelper.appStatusMapping[key];

	response.setBody(responseBody);
	response.setStatus(200);

})(request, response);
