sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	// Setting the HTTP method to POST so that we can utilize setting requestBody when calling the API.
	// We need to send over the lastProcessedDictionary as part of the request, and this could exceed
	// queryParams' character limit.

	try {
		if (!request.body && !request.body.data.lastProcessedDictionary) {
			throw new sn_ws_err.ServiceError()
				.setStatus(422)
				.setMessage(gs.getMessageLang('Unable to query last processed table.', "en"))
				.setDetail(gs.getMessage('Unable to query last processed table.'));
		}

		var aesUtils = new AesUtils();

		var sysUserHasRoleGR = new GlideRecord('sys_user_has_role');
		sysUserHasRoleGR.addQuery('role', SyncConstants.AES_USER_ROLE_ID);
		sysUserHasRoleGR.query();

		var lastProcessedDictionary = request.body.data.lastProcessedDictionary;
		var usersWithAESRole = [];

		while (sysUserHasRoleGR.next()) {
			var sysUserHasRole = aesUtils.getObjectFromGlideRecord(sysUserHasRoleGR, ['sys_updated_on', 'user']);
			var sysUserHasRoleUpdatedOn = new GlideDateTime(sysUserHasRole.sys_updated_on);
			var lastProcessedForUser = new GlideDateTime(lastProcessedDictionary[sysUserHasRole.user]);
			var newlyAddedAESUser = lastProcessedDictionary[sysUserHasRole.user] === undefined;
			var dateEligible = sysUserHasRoleUpdatedOn.onOrAfter(lastProcessedForUser);
			var userWithAESRole = aesUtils.getObjectFromGlideRecord(sysUserHasRoleGR.user.getRefRecord(), ['sys_id', 'first_name', 'password_needs_reset', 'email', 'active']);

			if (userWithAESRole.active === '1' && (dateEligible || newlyAddedAESUser)) {
				usersWithAESRole.push(userWithAESRole);
			}
		}

		response.setBody({
			sysUsers: usersWithAESRole,
		});

		response.setStatus(200);
	} catch(e) {
		throw new sn_ws_err.ServiceError()
			.setStatus(500)
			.setMessage(gs.getMessageLang('Unable to find sys_user with AES roles.', "en"))
			.setDetail(gs.getMessage('Unable to find sys_user with AES roles.'));
	}

})(request, response);
