sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var requestData = request.body.data;

	if(!requestData) {
		response.setStatus(200);
		response.setBody({synced: [], failed: []});
		return;
	}

	var queueItems = requestData.map(function (item) {
		return (
			new SyncQueueItem({
				sysId: item.sys_id,
				operation: item.operation,
				table: item.table,
				payload: item.payload
			})
		);
	});

	var syncService = new UserSyncService();

	var fnMap = {
		'sys_user': {
			'insert': syncService.userInsert.bind(syncService),
			'update': syncService.userUpdate.bind(syncService),
			'delete': syncService.userDelete.bind(syncService)
		},
		'sys_user_grmember': {
			'insert': syncService.groupInsert.bind(syncService),
			'update': syncService.operationNotImplemented('UPDATE').bind(syncService),
			'delete': syncService.groupDelete.bind(syncService),

		}
	};

	var responseBody = { synced: [], failed: [], skipped: [] };

	var addToResponseBody = function(processedQueueItem) {
		if(!processedQueueItem) { return; }

		var status = processedQueueItem.getStatus();

		if (responseBody.hasOwnProperty(status.state)) {
			responseBody[status.state].push(status);
		}
	};

	queueItems.forEach(function (queuedItem) {
		if (!queuedItem.isValid()) {
			queuedItem.setState(SyncConstants.FAILED, 'Invalid queue item, insufficent information provided.');
			addToResponseBody(queuedItem);
			return;
		}

		var operationFn = fnMap[queuedItem.table][queuedItem.operation];
		var processedQueueItem = operationFn(queuedItem);

		addToResponseBody(processedQueueItem);
	});

	response.setStatus(200);
	response.setBody(responseBody);

})(request, response);
