sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var aesUtils = new AesUtils();
    var deploymentHelpers = new DeploymentHelpers();
    var activePipelineRecord;

    try {
        activePipelineRecord = deploymentHelpers.getActivePipelineRecord();
    } catch (e) {
		throw new sn_ws_err.ServiceError()
			.setStatus(404)
			.setMessage(gs.getMessageLang("The setup for the sn_app_eng_studio_pipeline table is incomplete. Please ensure that you have an active record, with dev_environment and prod_environment setup.", "en"))
			.setDetail(gs.getMessage("The setup for the sn_app_eng_studio_pipeline table is incomplete. Please ensure that you have an active record, with dev_environment and prod_environment setup."));
    }

    var urls = {
        currentInstanceUrl: gs.getProperty('glide.servlet.uri'),
        prodInstanceUrl: activePipelineRecord.prod_environment.getRefRecord().getValue('instance_url'),
        devInstanceUrl: activePipelineRecord.dev_environment.getRefRecord().getValue('instance_url'),
    };

    // remove trailing '/' before comparing the user's currentInstanceUrl with the prodInstanceUrl
    for (url in urls) {
        urls[url] = aesUtils.removeTrailingSlash(urls[url]);
    }

    if (!urls.prodInstanceUrl || !urls.devInstanceUrl) {
		throw new sn_ws_err.ServiceError()
			.setStatus(500)
			.setMessage(gs.getMessageLang("The setup for the sn_app_eng_studio_pipeline table is incomplete. Please ensure that you have an active record, with dev_environment and prod_environment setup.", "en"))
			.setDetail(gs.getMessage("The setup for the sn_app_eng_studio_pipeline table is incomplete. Please ensure that you have an active record, with dev_environment and prod_environment setup."));
    }

    if (urls.prodInstanceUrl === urls.devInstanceUrl) {
		throw new sn_ws_err.ServiceError()
			.setStatus(500)
			.setMessage(gs.getMessageLang("The setup for the sn_app_eng_studio_pipeline table is incorrect. The dev_environment and prod_environment cannot be the same.", "en"))
			.setDetail(gs.getMessage("The setup for the sn_app_eng_studio_pipeline table is incorrect. The dev_environment and prod_environment cannot be the same."));
    }

    response.setBody({
        isProdInstance: urls.currentInstanceUrl === urls.prodInstanceUrl,
        currentInstanceUrl: urls.currentInstanceUrl,
        prodUrl: urls.prodInstanceUrl,
        devUrl: urls.devInstanceUrl,
    });

    response.setStatus(200);

})(request, response);
