sys_ws_operation.operation_script:
// Receives a salted key and does the lookup for the salted key in request_authorization_key table. This endpoint
// most likely executes on the production environment.

(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var requestHeaderAccessToken = request.headers['x-access-token'];

	if(!requestHeaderAccessToken) {
		response.setStatus(200);
		response.setBody({isAuthorized: false});
		return;
	}

	var authService = new RequestAuthService();

	var hasKey;
	try {
		// Answers the question. Did this instance send this request?
		hasKey = authService.hasKey(requestHeaderAccessToken);
	}
	catch(ex) {
		throw new sn_ws_err.ServiceError()
			.setStatus(500)
			.setMessage(gs.getMessageLang("Unhandled exception {0}", "en", [ex.toString()]))
			.setDetail(gs.getMessage("Unhandled exception {0}", [ex.toString()]));
	}

	response.setStatus(200);
	response.setBody({isAuthorized: hasKey});


})(request, response);
