sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var deploymentHelpers = new DeploymentHelpers();
    var aesUtils = new AesUtils();
    var prodCredentials = deploymentHelpers.getProdCredentials();
    var prodBaseInstanceUrl = prodCredentials.prodBaseInstanceUrl;
    var credentialID = deploymentHelpers.getCredentialID(); // discovery_credential.sys_id

    if (credentialID) {
        var path = '/api/sn_app_eng_studio/generic/configuration';
        var remoteInstanceResponse = aesUtils.getResponseFromRemoteInstanceFlow(prodBaseInstanceUrl, path, credentialID);

        if (remoteInstanceResponse && remoteInstanceResponse.has_error) {
            gs.info("Error occurred in sys_ws_operation: Get AES Prod Configuration\n" + remoteInstanceResponse.error_message);

            response.setStatus(remoteInstanceResponse.status_code);
            response.setBody({
                message: remoteInstanceResponse.error_message
            });
        } else if (remoteInstanceResponse && remoteInstanceResponse.body) {
            var configuration = JSON.parse(remoteInstanceResponse.body).result;

            response.setBody(configuration);
            response.setStatus(remoteInstanceResponse.status_code);
        } else {
            response.setStatus(404);
            response.setBody({
                message: gs.getMessage("Prod Credentials not found")
            });
        }
    } else {
        response.setStatus(404);
        response.setBody({
            message: gs.getMessage("Prod Credentials not found")
        });
    }
})(request, response);
