sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    try {
        var sort_field = request.queryParams.sort_field ? request.queryParams.sort_field[0].toLowerCase() : '';
        var sort_order = request.queryParams.sort_order ? request.queryParams.sort_order[0].toLowerCase() : '';

        // array of sys_ids
        var userAppSysIds = new ApplicationsListBuilder()
			// FIXME: check if filterOut actually does something
            .filterOut([CreatorStudioConstants.GLOBAL_APPLICATION_SYS_ID])
            .sortBy(sort_field, sort_order)
            .getUserApps();

        var decoratedApplicationList = [];

        userAppSysIds.forEach(function(userAppSysId) {            
			/**
			 * Checks that the application is not in the global scope
			 * before sending to the FE; as requested in STRY52334107
			 */			
            if (!AccessValidator.isApplicationInGlobalScope(userAppSysId)) {
                decoratedApplicationList.push(ApplicationsDataModel.generate(userAppSysId));
            }
        });

        response.setBody(decoratedApplicationList);
        response.setStatus(200);
    } catch (exception) {
        throw new sn_ws_err.ServiceError()
            .setStatus(500)
            .setMessage(gs.getMessageLang("Unhandled exception {0}", "en", [exception]))
            .setDetail(gs.getMessage("Unhandled exception {0}", [exception]));
    }
})(request, response);
