sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var appSysId = request.pathParams.application_sys_id;
    try {
        // if needed, the below function can be updated to pass in request body data
        // in the future
        var appDetails = AppDetailsService.updateAppDetails(appSysId, request.body.data);
    } catch (exception) {
        switch (exception) {
            case CreatorStudioConstants.errors.APPLICATION_DOES_NOT_EXIST:
                throw new sn_ws_err.ServiceError()
                    .setStatus(404)
                    .setMessage(gs.getMessageLang("Application with sys_id '{0}' does not exist.", "en", appSysId))
                    .setDetail(gs.getMessage("Application with sys_id '{0}' does not exist.", appSysId));
            case CreatorStudioConstants.errors.APPLICATION_ACCESS_DENIED:
                throw new sn_ws_err.ServiceError()
                    .setStatus(403)
                    .setMessage(gs.getMessageLang("Access denied to application with sys_id '{0}'.", "en", appSysId))
                    .setDetail(gs.getMessage("Access denied to application with sys_id '{0}'", appSysId));
            case CreatorStudioConstants.errors.APPLICATION_DETAILS_RECORD_NOT_FOUND:
                throw new sn_ws_err.ServiceError()
                    .setStatus(404)
                    .setMessage(gs.getMessageLang("The {0} record for application with sys_id '{1}' could not be found.", "en", [CreatorStudioConstants.appDetailsTable.NAME, appSysId]))
                    .setDetail(gs.getMessage("The {0} record for application with sys_id '{1}' could not be found.", [CreatorStudioConstants.appDetailsTable.NAME, appSysId]));
            case CreatorStudioConstants.errors.UPDATE_FAILURE:
                throw new sn_ws_err.ServiceError()
                    .setStatus(500)
                    .setMessage(gs.getMessageLang("Failed to update the {0} record for application with sys_id '{1}'.", "en", [CreatorStudioConstants.appDetailsTable.NAME, appSysId]))
                    .setDetail(gs.getMessage("Failed to update the {0} record for application with sys_id '{1}'.", [CreatorStudioConstants.appDetailsTable.NAME, appSysId]));
            default:
                throw new sn_ws_err.ServiceError()
                    .setStatus(500)
                    .setMessage(gs.getMessageLang("Unhandled exception {0}", "en", exception))
                    .setDetail(gs.getMessage("Unhandled exception {0}", exception));
        }
    }

    response.setBody(AppDetailsBuilder.buildDetailsBody(appDetails));
    response.setStatus(200);

})(request, response);
